	function isValidMobile($mob) {		$num = trim($mob);		$arr_a = array("-","."," ","(",")");		$arr_b = array("","","","","");		$num = str_replace($arr_a, $arr_b, $num);		if ((strlen($num) < 10) || (strlen($num) > 11)) return false;		$num = (strlen($num) == 11) ? $num : ("1$num");			if ((strlen($num) == 11) && (substr($num, 0, 1) == "1")) {			return $num;		} else {			return false;		}	}?>